CREATE FUNCTION dbo.NPF_CLEAR_SKILL_TREE (
  @p_type VARCHAR(64) = NULL,
  @p_value VARBINARY(MAX) = NULL
)
RETURNS VARBINARY(MAX)
AS
BEGIN
  DECLARE @p_mt3 VARBINARY(360)         = 
  DECLARE @p_mt4 VARBINARY(480)         = 
  DECLARE @p_mt4_passive VARBINARY(160) = 
  DECLARE @p_return VARBINARY(MAX) = NULL
  IF @p_type = 'mt3' BEGIN
    SET @p_return = @p_mt3
  END
  IF @p_type = 'mt4' BEGIN
    SET @p_return = @p_mt4
  END
  IF @p_type = 'mt4.passive' BEGIN
    SET @p_return = @p_mt4_passive
  END
  RETURN @p_return
END